#include "libft.h"

void		*ft_memset(void *b, int c, size_t len)
{
	size_t	i;
	char	*b_ptr;

	i = 0;
	b_ptr = (char *)b;
	while (i < len)
	{
		b_ptr[i] = c;
		i++;
	}
	return (b);
}
