#include "libft.h"

char	*ft_strcat(char *s1, const char *s2)
{
	int			i;
	int			j;

	if (!s1 || !s2)
		return (NULL);
	i = -1;
	j = (int)ft_strlen(s1);
	while (*(s2 + ++i))
		*(s1 + j++) = *(s2 + i);
	*(s1 + j) = '\0';
	return (s1);
}
